<example module="ngBindHtmlExample" deps="angular-sanitize.js">
  <file name="index.html">
    <div ng-controller="ngBindHtmlCtrl">
     <p ng-bind-html="myHTML"></p>
    </div>
  </file>

  <file name="script.js">
